import React from "react";
import ReactDOM from "react-dom";
import DemoData from './Data'
import Basic from "./Basic";
import Basic0 from "./Basic0"
import Basic1 from "./Basic1"
import Basic2 from "./Basic2"
import Basic3 from "./Basic3"
import Basic4 from "./Basic4"
import Basic5 from "./Basic5"

const mondayRootElement = document.getElementById("monday_root");
// Add the calendar for Tuesday
const tuesdayRootElement = document.getElementById("tuesday_root");
ReactDOM.render(
  <React.StrictMode>
    <Basic0 />
  </React.StrictMode>,
  tuesdayRootElement
)

// Add the calendar for Wednesday
const wednesdayRootElement = document.getElementById("wednesday_root");
ReactDOM.render(
  <React.StrictMode>
    <Basic1 />
  </React.StrictMode>,
  wednesdayRootElement
)

// Add the calendar for Thursday
const thursdayRootElement = document.getElementById("thursday_root");
ReactDOM.render(
  <React.StrictMode>
    <Basic2 />
  </React.StrictMode>,
  thursdayRootElement
)

// Add the calendar for Friday
const fridayRootElement = document.getElementById("friday_root");
ReactDOM.render(
  <React.StrictMode>
    <Basic3 />
  </React.StrictMode>,
  fridayRootElement
)

// Add the calendar for Saturday
const saturdayRootElement = document.getElementById("saturday_root");
ReactDOM.render(
  <React.StrictMode>
    <Basic4 />
  </React.StrictMode>,
  saturdayRootElement
)

// Add the calendar for Sunday
const sundayRootElement = document.getElementById("sunday_root");
ReactDOM.render(
  <React.StrictMode>
    <Basic5 />
  </React.StrictMode>,
  sundayRootElement
)

// Remove unwanted calendar header for Monday
var monday = document.getElementById("monday_root").childNodes[0].childNodes[0].childNodes[0];
monday.removeChild(monday.childNodes[0]);

// Remove unwanted calendar header for Tuesday
var tuesday = document.getElementById("tuesday_root").childNodes[0].childNodes[0].childNodes[0];
tuesday.removeChild(tuesday.childNodes[0]);

// Remove unwanted calendar header for Wednesday
var wednesday = document.getElementById("wednesday_root").childNodes[0].childNodes[0].childNodes[0];
wednesday.removeChild(wednesday.childNodes[0]);

// Remove unwanted calendar header for Thursday
var thursday = document.getElementById("thursday_root").childNodes[0].childNodes[0].childNodes[0];
thursday.removeChild(thursday.childNodes[0]);

// Remove unwanted calendar header for Friday
var friday = document.getElementById("friday_root").childNodes[0].childNodes[0].childNodes[0];
friday.removeChild(friday.childNodes[0]);

//Remove unwanted calendar header for Saturday
var saturday = document.getElementById("saturday_root").childNodes[0].childNodes[0].childNodes[0];
saturday.removeChild(saturday.childNodes[0]);

// Remove unwanted calendar header for Sunday
var sunday = document.getElementById("sunday_root").childNodes[0].childNodes[0].childNodes[0];
sunday.removeChild(sunday.childNodes[0]);

function openDay(evt, dayName) {

   // Declare all variables
   var i, tabcontent, tablinks;

   // Get all elements with class=“tabcontent” and hide them
   tabcontent = document.getElementsByClassName("tabcontent");
   for (i = 0; i < tabcontent.length; i++) {
      tabcontent[i].style.display = "none";
   }

   // Get all elements with class=“tablinks” and remove the class “active”
   tablinks = document.getElementsByClassName("tablinks");
   for (i = 0; i < tablinks.length; i++) {
      tablinks[i].className = tablinks[i].className.replace("active", "");
   }

   // Show the current tab, and add an “active” class to the button that opened the tab
   document.getElementById(dayName).style.display = "block";
   evt.currentTarget.className += "active";
}

var mondaycalendar = document.getElementById("defaultOpen");
mondaycalendar.onclick = function() {
  openDay(window.event, 'Monday');
  // Add the calendar for Monday

  ReactDOM.render(
    <React.StrictMode>
      <Basic />
    </React.StrictMode>,
    mondayRootElement
  );
}

var tuesdaycalendar = document.getElementById("TuesdayCalendar");
tuesdaycalendar.onclick = function() {
  openDay(window.event, 'Tuesday');
}

var wednesdaycalendar = document.getElementById("WednesdayCalendar");
wednesdaycalendar.onclick = function() {
  openDay(window.event, 'Wednesday');
}

var thursdaycalendar = document.getElementById("ThursdayCalendar");
thursdaycalendar.onclick = function() {
  openDay(window.event, 'Thursday');
}

var fridaycalendar = document.getElementById("FridayCalendar");
fridaycalendar.onclick = function() {
  openDay(window.event, 'Friday');
}

var saturdaycalendar = document.getElementById("SaturdayCalendar");
saturdaycalendar.onclick = function() {
  openDay(window.event, 'Saturday')
}

var sundaycalendar = document.getElementById("SundayCalendar");
sundaycalendar.onclick = function() {
  openDay(window.event, 'Sunday')
}
