import React from "react";

class Checkbox extends React.Component {
  render() {
    if (this.props.option1 === "Desktop") {var option1id = "r1"}
    if (this.props.option1 === "Monitor") {var option1id = "r2"}
    if (this.props.option1 === "Laptop") {var option1id = "r3"}
    if (this.props.option1 === "Task Lamp") {var option1id = "r4"}
    if (this.props.option1 === "Fan") {var option1id = "r5"}
    if (this.props.option2 === "Desktop") {var option2id = "r1"}
    if (this.props.option2 === "Monitor") {var option2id = "r2"}
    if (this.props.option2 === "Laptop") {var option2id = "r3"}
    if (this.props.option2 === "Task Lamp") {var option2id = "r4"}
    if (this.props.option2 === "Fan") {var option2id = "r5"}
    if (this.props.option3 === "Desktop") {var option3id = "r1"}
    if (this.props.option3 === "Monitor") {var option3id = "r2"}
    if (this.props.option3 === "Laptop") {var option3id = "r3"}
    if (this.props.option3 === "Task Lamp") {var option3id = "r4"}
    if (this.props.option3 === "Fan") {var option3id = "r5"}
    if (this.props.option4 === "Desktop") {var option4id = "r1"}
    if (this.props.option4 === "Monitor") {var option4id = "r2"}
    if (this.props.option4 === "Laptop") {var option4id = "r3"}
    if (this.props.option4 === "Task Lamp") {var option4id = "r4"}
    if (this.props.option4 === "Fan") {var option4id = "r5"}

    return (
      <>
        <h3> Also apply to: </h3>
        <input
          type="checkbox"
          name={this.props.name}
          value={option1id}
        />
        <label htmlFor={this.props.option1}> {this.props.option1} </label>
        <br />
        <input
          type="checkbox"
          name={this.props.name}
          value={option2id}
        />
        <label htmlFor={this.props.option2}> {this.props.option2} </label>
        <br />
        <input
          type="checkbox"
          name={this.props.name}
          value={option3id}
        />
        <label htmlFor={this.props.option3}> {this.props.option3} </label>
        <br />
        <input
          type="checkbox"
          name={this.props.name}
          value={option4id}
        />
        <label htmlFor={this.props.option4}> {this.props.option4} </label>
      </>
    );
  }
}

export default Checkbox;
