import React, { Component } from "react";
import ReactDOM, { render } from 'react-dom'
import moment from 'moment'
import Scheduler, {
  SchedulerData,
  ViewTypes
} from "react-big-scheduler";
import DemoData from './Data.js';
import withDragDropContext from "./withDnDContext";
import "react-big-scheduler/lib/css/style.css";
import Timepicker from './Timepicker';
import Radio from './Radio'
import Checkbox from './Checkbox'
import './styles.css'

class Basic extends Component {
  constructor(props) {
    super(props);

    let schedulerData = new SchedulerData(
      "2017-12-18",
      ViewTypes.Day,
      false,
      false,
      {
        // minuteStep: 15
        checkConflict: true,
        recurringEventsEnabled: true,
        scrollToSpecialMomentEnabled: true,
        crossResourceMove: false
      }, {
        getScrollSpecialMomentFunc: this.getScrollSpecialMoment,
        isNonWorkingTimeFunc: this.isNonWorkingTime
      }
    );
    // schedulerData.localeMoment.locale('en');
    schedulerData.setResources(DemoData.resources);
    schedulerData.setEvents(DemoData.events);
    this.state = {
      viewModel: schedulerData
    };
    window.mondayCalendar = this
  }
  render() {
    const { viewModel } = this.state;
    return (
      <div>
        <div>
          <Scheduler
            schedulerData={viewModel}
            eventItemClick={this.eventClicked}
            viewEventClick={this.ops1}
            viewEventText="Delete"
            updateEventStart={this.updateEventStart}
            updateEventEnd={this.updateEventEnd}
            moveEvent={this.moveEvent}
            newEvent={this.newEvent}
            conflictOccurred={this.conflictOccurred}
          />
        </div>
      </div>
    );
  }

  isNonWorkingTime = (schedulerData, time) => {
    return true;
  }

  // Start from 7 AM
  getScrollSpecialMoment = (schedulerData, startMoment, endMoment) => {
    return moment("2017-12-18 07:00:00");
  }

  eventClicked = (schedulerData, event) => {
    function okButtonClicked() {
      // Update schedule name
      var schedule = document.getElementById("schedule_name").value;
      var start = document.getElementById("start").value;
      var end = document.getElementById("end").value;
      event.title = schedule + " (" + start + " - " + end + ")";


      // Update start time

      if (start.length === 7) {
        start = "0" + start;
      }
      if (start.includes("AM")) {
        if (start.substring(0, 2) === "12") {
          start = "2017-12-18 00" + start.substring(2, 5) + ":00"
        } else {
          start = "2017-12-18 " + start.substring(0, 5) + ":00"
        }
      } else {
        if (start.substring(0, 2) === "12") {
          start = "2017-12-18 " + start.substring(0, 5) + ":00"
        } else {
          start = "2017-12-18 " + (Number(start.substring(0, 2)) + 12).toString() + start.substring(2, 5) + ":00"
        }
      }

      schedulerData.updateEventStart(event, start);

      // Update end time

      if (end.length === 7) {
        end = "0" + end;
      }
      if (end.includes("AM")) {
        if (end.substring(0, 2) === "12") {
          end = "2017-12-18 00" + end.substring(2, 5) + ":00"
        } else {
          end = "2017-12-18 " + end.substring(0, 5) + ":00"
        }
      } else {
        if (end.substring(0, 2) === "12") {
          end = "2017-12-18 " + end.substring(0, 5) + ":00"
        } else {
          end = "2017-12-18 " + (Number(end.substring(0, 2)) + 12).toString() + end.substring(2, 5) + ":00"
        }
      }
      schedulerData.updateEventEnd(event, end);


      var repeat_option = document.querySelector('input[name="Repeat:"]:checked').value;
      var rrule = ""
      if (repeat_option === "Every Day") {
        event.rrule="FREQ=DAILY;DTSTART=20171218T000000Z;UNTIL=20171224T235900Z"
        rrule="FREQ=DAILY;DTSTART=20171218T000000Z;UNTIL=20171224T235900Z"
      } else {
        event.rrule = ""
        rrule=""
      }

      var plugload_option = document.querySelectorAll('input[type="checkbox"]:checked');
      for (var plugload of plugload_option) {
        console.log(plugload.value)
        let newFreshId = 0;
        schedulerData.events.forEach(item => {
          if (item.id >= newFreshId) newFreshId = item.id + 1;
        });

        let newEvent = {
          id: newFreshId,
          title: event.title,
          start: moment(start),
          end: moment(end),
          resourceId: plugload.value,
          bgColor: "rgb(128, 197, 246)",
          showPopover: false,
          rrule: rrule
        };
        schedulerData.addEvent(newEvent); // Check why new event disappears
      }

      if (start >= end || end <= start) {
        alert("Check the timings! Start time should be before end time.")
      } else {
        // Update displayed calendar
        window.mondayCalendar.setState({
          viewModel: schedulerData
        })

        // Remove popup
        ReactDOM.unmountComponentAtNode(document.getElementById("popup-container"));
      }
    }

    function deleteButtonClicked() {
      schedulerData.removeEvent(event)
      window.mondayCalendar.setState({
        viewModel: schedulerData
      })
      ReactDOM.unmountComponentAtNode(document.getElementById("popup-container"));
    }

    var resourceName = schedulerData.getSlotById(event.resourceId).name
    render(<>
              <div className="container">
                <input id="schedule_name" type="text" name="title" defaultValue={event.title.substring(0, event.title.length - 22)} />
                <Timepicker defaultStart={moment(event.start)} defaultEnd={moment(event.end)} />
                <Radio name="Repeat:" day="Monday" />
                {(resourceName === "Desktop") && <Checkbox
                  name="Plug Loads"
                  option1="Monitor"
                  option2="Laptop"
                  option3="Task Lamp"
                  option4="Fan"
                />}

                {(resourceName === "Monitor") && <Checkbox
                  name="Plug Loads"
                  option1="Desktop"
                  option2="Laptop"
                  option3="Task Lamp"
                  option4="Fan"
                />}

                {(resourceName === "Laptop") && <Checkbox
                  name="Plug Loads"
                  option1="Desktop"
                  option2="Monitor"
                  option3="Task Lamp"
                  option4="Fan"
                />}

                {(resourceName === "Task Lamp") && <Checkbox
                  name="Plug Loads"
                  option1="Desktop"
                  option2="Monitor"
                  option3="Laptop"
                  option4="Fan"
                />}

                {(resourceName === "Fan") && <Checkbox
                  name="Plug Loads"
                  option1="Desktop"
                  option2="Monitor"
                  option3="Laptop"
                  option4="Task Lamp"
                />}
                <br />
                <br />
                <button onClick={deleteButtonClicked}> Delete </button>
                <button onClick={okButtonClicked}> OK </button>
              </div>
          </>, document.getElementById("popup-container"));
  };

  ops1 = (schedulerData, event) => {
    schedulerData.removeEvent(event);
    this.setState({
      viewModel: schedulerData
    })
  };

  newEvent = (schedulerData, slotId, slotName, start, end, type, item) => {
    let newFreshId = 0;
    schedulerData.events.forEach(item => {
      if (item.id >= newFreshId) newFreshId = item.id + 1;
    });

    let newEvent = {
      id: newFreshId,
      title: "(no name)",
      start: start,
      end: end,
      resourceId: slotId,
      bgColor: "rgb(128, 197, 246)",
      showPopover: false
    };
    schedulerData.addEvent(newEvent);
    this.setState({
      viewModel: schedulerData
    })
    function okButtonClicked() {
      // Update schedule name
      var schedule = document.getElementById("schedule_name").value;
      newEvent.title = schedule;

      // Update start time
      var start = document.getElementById("start").value;
      if (start.length === 7) {
        start = "0" + start;
      }
      if (start.includes("AM")) {
        if (start.substring(0, 2) === "12") {
          start = "2017-12-18 00" + start.substring(2, 5) + ":00"
        } else {
          start = "2017-12-18 " + start.substring(0, 5) + ":00"
        }
      } else {
        if (start.substring(0, 2) === "12") {
          start = "2017-12-18 " + start.substring(0, 5) + ":00"
        } else {
          start = "2017-12-18 " + (Number(start.substring(0, 2)) + 12).toString() + start.substring(2, 5) + ":00"
        }
      }

      schedulerData.updateEventStart(newEvent, start);

      // Update end time
      var end = document.getElementById("end").value;
      if (end.length === 7) {
        end = "0" + end;
      }
      if (end.includes("AM")) {
        if (end.substring(0, 2) === "12") {
          end = "2017-12-18 00" + end.substring(2, 5) + ":00"
        } else {
          end = "2017-12-18 " + end.substring(0, 5) + ":00"
        }
      } else {
        if (end.substring(0, 2) === "12") {
          end = "2017-12-18 " + end.substring(0, 5) + ":00"
        } else {
          end = "2017-12-18 " + (Number(end.substring(0, 2)) + 12).toString() + end.substring(2, 5) + ":00"
        }
      }
      schedulerData.updateEventEnd(newEvent, end);


      var repeat_option = document.querySelector('input[name="Repeat:"]:checked').value;
      if (repeat_option === "Every Day") {
        newEvent.rrule="FREQ=DAILY;DTSTART=20171218T000000Z;UNTIL=20171224T235900Z"
      } else {
        newEvent.rrule = ""
      }

      if (start >= end || end <= start) {
        alert("Check the timings! Start time should be before end time.")
      } else {
        // Update displayed calendar
        window.mondayCalendar.setState({
          viewModel: schedulerData
        })

        // Remove popup
        ReactDOM.unmountComponentAtNode(document.getElementById("popup-container"));
      }
    }

    function deleteButtonClicked() {
      schedulerData.removeEvent(newEvent)
      window.mondayCalendar.setState({
        viewModel: schedulerData
      })
      ReactDOM.unmountComponentAtNode(document.getElementById("popup-container"));
    }

    var resourceName = schedulerData.getSlotById(newEvent.resourceId).name
    render(<>
              <div className="container">
                <input id="schedule_name" type="text" name="title" defaultValue={newEvent.title} />
                <Timepicker defaultStart={moment(newEvent.start)} defaultEnd={moment(newEvent.end)} />
                <Radio name="Repeat:" day="Monday" />
                {(resourceName === "Desktop") && <Checkbox
                  name="Plug Loads"
                  option1="Monitor"
                  option2="Laptop"
                  option3="Task Lamp"
                  option4="Fan"
                />}

                {(resourceName === "Monitor") && <Checkbox
                  name="Plug Loads"
                  option1="Desktop"
                  option2="Laptop"
                  option3="Task Lamp"
                  option4="Fan"
                />}

                {(resourceName === "Laptop") && <Checkbox
                  name="Plug Loads"
                  option1="Desktop"
                  option2="Monitor"
                  option3="Task Lamp"
                  option4="Fan"
                />}

                {(resourceName === "Task Lamp") && <Checkbox
                  name="Plug Loads"
                  option1="Desktop"
                  option2="Monitor"
                  option3="Laptop"
                  option4="Fan"
                />}

                {(resourceName === "Fan") && <Checkbox
                  name="Plug Loads"
                  option1="Desktop"
                  option2="Monitor"
                  option3="Laptop"
                  option4="Task Lamp"
                />}
                <br />
                <br />
                <button onClick={deleteButtonClicked}> Delete </button>
                <button onClick={okButtonClicked}> OK </button>
              </div>
          </>, document.getElementById("popup-container"));
  };

  updateEventStart = (schedulerData, event, newStart) => {
    function okButtonClicked() {
      // Update schedule name
      var schedule = document.getElementById("schedule_name").value;
      event.title = schedule;

      // Update start time
      var start = document.getElementById("start").value;
      if (start.length === 7) {
        start = "0" + start;
      }
      if (start.includes("AM")) {
        if (start.substring(0, 2) === "12") {
          start = "2017-12-18 00" + start.substring(2, 5) + ":00"
        } else {
          start = "2017-12-18 " + start.substring(0, 5) + ":00"
        }
      } else {
        if (start.substring(0, 2) === "12") {
