    // If OFF, image and outer ring should be grey
    laptopImage.src = "../Images/Laptop OFF.png";
    laptopRing.classList.remove("green");
    laptopRing.classList.add("grey");

    // If at least one plug load is ON, master button is automatically toggled to ON.
    // If all plug loads are OFF, master button is automatically toggled to OFF.
    Main()
  }
}

// Define function for when task lamp component is clicked
tasklamp.onclick = function() {
  // Toggle the switch
  document.getElementById("TaskLamp").checked = !document.getElementById("TaskLamp").checked

  // Get the current state (ON/OFF) of the plug load
  var current_state = document.getElementById("TaskLamp").checked

  if (current_state === true) {
    // If ON, image and outer ring should be green
    tasklampImage.src = "../Images/Task Lamp ON.png";
    tasklampRing.classList.remove("grey");
    tasklampRing.classList.add("green");

    // If at least one plug load is ON, master button is automatically toggled to ON.
    // If all plug loads are OFF, master button is automatically toggled to OFF.
    Main()
  } else {
    // If OFF, image and outer ring should be grey
    tasklampImage.src = "../Images/Task Lamp OFF.png";
