  }
}

// Define the function for when fan component is clicked
fan.onclick = function() {
  // Toggle the switch
  document.getElementById("Fan").checked = !document.getElementById("Fan").checked

  // Get the current state (ON/OFF) of the plug load
  var current_state = document.getElementById("Fan").checked

  if (current_state === true) {
    // If ON, image and outer ring should be green
    fanImage.src = "../Images/Fan ON.png";
    fanRing.classList.remove("grey");
    fanRing.classList.add("green");

    // If at least one plug load is ON, master button is automatically toggled to ON.
    // If all plug loads are OFF, master button is automatically toggled to OFF.
    Main()
  } else {
    // If OFF, image and outer ring should be grey
    fanImage.src = "../Images/Fan OFF.png";
    fanRing.classList.remove("green");
    fanRing.classList.add("grey");

    // If at least one plug load is ON, master button is automatically toggled to ON.
    // If all plug loads are OFF, master button is automatically toggled to OFF.
    Main()
  }
}


function Main() {
  // If at least one plug load is ON, master button is automatically toggled to ON.
  if (document.getElementById("Desktop").checked || document.getElementById("Monitor").checked || document.getElementById("Laptop").checked || document.getElementById("TaskLamp").checked || document.getElementById("Fan").checked) {
    document.getElementById("master").checked = true;
  } else {
    // If all plug loads are OFF, master button is automatically toggled to OFF.
    if (!document.getElementById("Desktop").checked && !document.getElementById("Monitor").checked && !document.getElementById("Laptop").checked && !document.getElementById("TaskLamp").checked && !document.getElementById("Fan").checked) {
      document.getElementById("master").checked = false;
    }
  }
}

