import "./styles.css"


import ToggleButton from './ToggleButton';
import Box from './Box';
import './index.css'

ReactDOM.render(
  <React.StrictMode>
    <>
      <h1 style={{textAlign: "center", fontFamily: "Helvetica"}}> Remote Control </h1>
      <img id="infoIcon" src="../Images/Info.png" alt="info"/>
      <div id="infoBox"> </div>

      <div style={{position: "absolute", right: "50px", top: "20px"}}>
        <p style={{fontFamily: "Helvetica", marginLeft: "50px", marginBottom:"5px"}}> Master </p>
        <ToggleButton id="master" defaultChecked="true" />
      </div>
      <br />
      <br />

      <div style={{position: "absolute", left:"15%", right: "15%", top: "105px"}}>
        <Box id="Desktop" />
        <Box id="Monitor"/>
        <Box id="Laptop" />
        <Box id="TaskLamp"/>
        <Box id="Fan" />
      </div>
    </>
  </React.StrictMode>,
  document.getElementById('root')
);

// Define necessary variables
var master = document.getElementById("master"); // Master toggle button
var desktop = document.getElementById("DesktopBox"); // Final component for desktop
var desktopImage = document.getElementById("DesktopIcon").childNodes[0].childNodes[0].childNodes[0]; // Icon image for desktop (to be changed between green and grey)
var desktopRing = document.getElementById("DesktopIcon").childNodes[0]; // Outer ring for desktop (to be changed between green and grey)
var monitor = document.getElementById("MonitorBox"); // Final component for monitor
var monitorImage = document.getElementById("MonitorIcon").childNodes[0].childNodes[0].childNodes[0]; // Icon image for monitor (to be changed between green and grey)
var monitorRing = document.getElementById("MonitorIcon").childNodes[0]; // Outer ring for monitor (to be changed between green and grey)
var laptop = document.getElementById("LaptopBox"); // Final component for laptop
var laptopImage = document.getElementById("LaptopIcon").childNodes[0].childNodes[0].childNodes[0]; // Icon image for laptop (to be changed between green and grey)
var laptopRing = document.getElementById("LaptopIcon").childNodes[0]; // Outer ring for laptop (to be changed between green and grey)
var tasklamp = document.getElementById("TaskLampBox"); // Final component for task lamp
var tasklampImage = document.getElementById("TaskLampIcon").childNodes[0].childNodes[0].childNodes[0]; // Icon image for task lamp (to be changed between green and grey)
var tasklampRing = document.getElementById("TaskLampIcon").childNodes[0]; // Outer ring for task lamp (to be changed between green and grey)
var fan = document.getElementById("FanBox"); // Final component for fan
var fanImage = document.getElementById("FanIcon").childNodes[0].childNodes[0].childNodes[0]; // Icon image for fan (to be changed between green and grey)
var fanRing = document.getElementById("FanIcon").childNodes[0]; // Outer ring for fan (to be changed between green and grey)

// Define function for when master button is clicked
master.onclick = function() {
  if (document.getElementById("master").checked === true) {
    // Switch ON all plug loads if master button is confirmed to be turned ON
    if (window.confirm("You are switching on all your plug loads.")) {
      // Switch desktop toggle button to ON
      document.getElementById("Desktop").checked = true;
      // Green image should be shown
      desktopImage.src = "../Images/Desktop ON.png";
      // Outer circle should be green
      desktopRing.classList.remove("grey");
      desktopRing.classList.add("green");

      // Switch monitor toggle button to ON
      document.getElementById("Monitor").checked = true;
      // Green image should be shown
      monitorImage.src = "../Images/Monitor ON.png";
      // Outer circle should be green
      monitorRing.classList.remove("grey");
      monitorRing.classList.add("green");

      // Switch laptop toggle button to ON
      document.getElementById("Laptop").checked = true;
      // Green image should be shown
      laptopImage.src = "../Images/Laptop ON.png";
      // Outer circle should be green
      laptopRing.classList.remove("grey");
      laptopRing.classList.add("green");

      // Switch task lamp toggle button to ON
      document.getElementById("TaskLamp").checked = true;
      // Green image should be shown
      tasklampImage.src = "../Images/Task Lamp ON.png";
      // Outer circle should be green
      tasklampRing.classList.remove("grey");
      tasklampRing.classList.add("green");

      // Switch fan toggle button to ON
      document.getElementById("Fan").checked = true;
      // Green image should be shown
      fanImage.src = "../Images/Fan ON.png";
      // Outer circle should be green
      fanRing.classList.remove("grey");
      fanRing.classList.add("green");
    } else {
      // Nothing happens if user does not confirm to switch ON all plug loads
      // Master button remains OFF
      document.getElementById("master").checked = false;
    }
  } else {
    // Switch OFF all plug loads if master button is confirmed to be turned OFF
    if (window.confirm("You are switching off all your plug loads.")) {
      // Switch desktop toggle button to OFF
      document.getElementById("Desktop").checked = false;
      // Grey image should be shown
      desktopImage.src = "../Images/Desktop OFF.png";
      // Outer circle should be grey
      desktopRing.classList.remove("green");
      desktopRing.classList.add("grey");

      // Switch monitor toggle button to OFF
      document.getElementById("Monitor").checked = false;
      // Grey image should be shown
      monitorImage.src = "../Images/Monitor OFF.png";
      // Outer ring should be grey
      monitorRing.classList.remove("green");
      monitorRing.classList.add("grey");

      // Switch laptop toggle button to OFF
      document.getElementById("Laptop").checked = false;
      // Grey image should be shown
      laptopImage.src = "../Images/Laptop OFF.png";
      // Outer ring should be grey
      laptopRing.classList.remove("green");
      laptopRing.classList.add("grey");

      // Switch task lamp toggle button to OFF
      document.getElementById("TaskLamp").checked = false;
      // Grey image should be shown
      tasklampImage.src = "../Images/Task Lamp OFF.png";
      // Outer ring should be grey
      tasklampRing.classList.remove("green");
      tasklampRing.classList.add("grey");

      // Switch fan toggle button to OFF
      document.getElementById("Fan").checked = false;
      // Grey image should be shown
      fanImage.src = "../Images/Fan OFF.png";
      // Outer ring should be grey
      fanRing.classList.remove("green");
      fanRing.classList.add("grey");
    } else {
      // Nothing happens if user does not confirm to switch OFF all plug loads
      // Master button remains ON
      document.getElementById("master").checked = true;
    }
  }
}


// Define function for when desktop component is clicked
desktop.onclick = function() {
  // Toggle the switch
  document.getElementById("Desktop").checked = !document.getElementById("Desktop").checked

  // Get the current state (ON/OFF) of the plug load
  var current_state = document.getElementById("Desktop").checked

  if (current_state === true) {
    // If ON, image and outer ring should be green
    desktopImage.src = "../Images/Desktop ON.png";
    desktopRing.classList.remove("grey");
