function Main() {
  // If at least one plug load is ON, master button is automatically toggled to ON.
  if (document.getElementById("Desktop").checked || document.getElementById("Monitor").checked || document.getElementById("Laptop").checked || document.getElementById("TaskLamp").checked || document.getElementById("Fan").checked) {
    document.getElementById("master").checked = true;
  } else {
    // If all plug loads are OFF, master button is automatically toggled to OFF.
    if (!document.getElementById("Desktop").checked && !document.getElementById("Monitor").checked && !document.getElementById("Laptop").checked && !document.getElementById("TaskLamp").checked && !document.getElementById("Fan").checked) {
