import React from 'react';

class CheckboxDays extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      default: this.props.default
    }
  }
  render() {
    return (
      <>
        <h3 style={{fontFamily:"Helvetica", fontSize:"12px"}}> Repeat: </h3>
        <div class="checkbox-container">
          {this.state.default.includes("MO") && <input type="checkbox" name="checkboxDays" value="Monday" defaultChecked/>}
          {!this.state.default.includes("MO") && <input type="checkbox" name="checkboxDays" value="Monday"/>}
          <label htmlFor="Monday" style={{fontFamily:"Helvetica"}}> Monday </label>
          <br />
          {this.state.default.includes("TU") && <input type="checkbox" name="checkboxDays" value="Tuesday" defaultChecked/>}
          {!this.state.default.includes("TU") && <input type="checkbox" name="checkboxDays" value = "Tuesday"/>}
          <label htmlFor="Tuesday" style={{fontFamily:"Helvetica"}}> Tuesday </label>
          <br />
          {this.state.default.includes("WE") && <input type="checkbox" name="checkboxDays" value="Wednesday" defaultChecked/>}
          {!this.state.default.includes("WE") && <input type="checkbox" name="checkboxDays" value="Wednesday"/>}
          <label htmlFor="Wednesday" style={{fontFamily:"Helvetica"}}> Wednesday </label>
          <br />
          {this.state.default.includes("TH") && <input type="checkbox" name="checkboxDays" value="Thursday" defaultChecked/>}
          {!this.state.default.includes("TH") && <input type="checkbox" name="checkboxDays" value="Thursday"/>}
          <label htmlFor="Thursday" style={{fontFamily:"Helvetica"}}> Thursday </label>
        </div>
        <div className="checkbox-container right">
          {this.state.default.includes("FR") && <input type="checkbox" name="checkboxDays" value="Friday" defaultChecked/>}
          {!this.state.default.includes("FR") && <input type="checkbox" name="checkboxDays" value="Friday"/>}
          <label htmlFor="Friday" style={{fontFamily:"Helvetica"}}> Friday </label>
          <br />
          {this.state.default.includes("SA") && <input type="checkbox" name="checkboxDays" value="Saturday" defaultChecked/>}
          {!this.state.default.includes("SA") && <input type="checkbox" name="checkboxDays" value="Saturday"/>}
          <label htmlFor="Saturday" style={{fontFamily:"Helvetica"}}> Saturday </label>
          <br />
          {this.state.default.includes("SU") && <input type="checkbox" name="checkboxDays" value="Sunday" defaultChecked/>}
          {!this.state.default.includes("SU") && <input type="checkbox" name="checkboxDays" value="Sunday"/>}
          <label htmlFor="Sunday" style={{fontFamily:"Helvetica"}}> Sunday </label>
        </div>
        <br />
      </>
    )
  }
}

export default CheckboxDays;
