import React, { Component } from "react";
import ReactDOM, { render } from "react-dom";
import moment from 'moment';
import Scheduler, {
  SchedulerData,
  ViewTypes
} from "react-big-scheduler";
import DemoData from './Data.js';
import withDragDropContext from "./withDnDContext";
import "react-big-scheduler/lib/css/style.css";
import Timepicker from './Timepicker';
import Radio from './Radio';
import Checkbox from './Checkbox';
import './styles.css';

class Basic extends Component {
  constructor(props) {
    super(props);

    //let schedulerData = new SchedulerData(new moment("2017-12-18").format(DATE_FORMAT), ViewTypes.Week);
    let schedulerData = new SchedulerData(
      "2017-12-19",
      ViewTypes.Day,
      false,
      false,
      {
        // minuteStep: 15
        checkConflict: true,
        recurringEventsEnabled: true,
        scrollToSpecialMomentEnabled: true,
        crossResourceMove: false
      }, {
        getScrollSpecialMomentFunc: this.getScrollSpecialMoment,
        isNonWorkingTimeFunc: this.isNonWorkingTime
      }
    );
    // schedulerData.localeMoment.locale('en');
    schedulerData.setResources(DemoData.resources);
    schedulerData.setEvents(DemoData.events);
    this.state = {
      viewModel: schedulerData
    };
    window.tuesdayCalendar = this
  }

  render() {
    const { viewModel } = this.state;
    return (
      <div>
        <div>
          <Scheduler
            schedulerData={viewModel}
            eventItemClick={this.eventClicked}
            viewEventClick={this.ops1}
            viewEventText="Delete"
            updateEventStart={this.updateEventStart}
            updateEventEnd={this.updateEventEnd}
            moveEvent={this.moveEvent}
            newEvent={this.newEvent}
          />
        </div>
      </div>
    );
  }


  eventClicked = (schedulerData, event) => {
    alert(
      `A popup should appear: {id: ${event.id}, title: ${event.title}}`
    );
  };

  ops1 = (schedulerData, event) => {
    schedulerData.removeEvent(event);
    this.setState({
      viewModel: schedulerData
    })
  };

  newEvent = (schedulerData, slotId, slotName, start, end, type, item) => {
    let newFreshId = 0;
    schedulerData.events.forEach(item => {
      if (item.id >= newFreshId) newFreshId = item.id + 1;
    });

    let newEvent = {
      id: newFreshId,
      title: "(no name)",
      start: start,
      end: end,
      resourceId: slotId,
      bgColor: "rgb(128, 197, 246)",
      showPopover: false
    };
    schedulerData.addEvent(newEvent);
    this.setState({
      viewModel: schedulerData
    });
  };

  updateEventStart = (schedulerData, event, newStart) => {
    alert(
      `A popup should appear: {id: ${event.id}, title: ${event.title}}`
    );
    schedulerData.updateEventStart(event, newStart);
    this.setState({
      viewModel: schedulerData
    });
  };

  updateEventEnd = (schedulerData, event, newEnd) => {
    alert(
      `A popup should appear: {id: ${event.id}, title: ${event.title}}`
    );
    schedulerData.updateEventEnd(event, newEnd);
    this.setState({
      viewModel: schedulerData
    });
  };

  moveEvent = (schedulerData, event, slotId, slotName, start, end) => {
    alert(
      `A popup should appear: {id: ${event.id}, title: ${event.title}}`
    );
    schedulerData.moveEvent(event, slotId, slotName, start, end);
    this.setState({
      viewModel: schedulerData
    });
  };
}

export default withDragDropContext(Basic);
